mapper.insert:
insert into purchase  (purchase_no, purchase_id, book_id, purchase_reason, application_data,application_state, reader_tel,reader_address,remark) 
values 
(#{purchaseNo}, #{purchaseId}, #{bookId}, #{purchaseReason}, #{applicationData},#{applicationState},#{readerTel},#{readerAddress},#{remark});

mapper.update:
update purchase
purchase_id=#{purchaseId}, book_id = #{bookId}, purchase_reason=#{purchaseReason}, application_data=#{applicationData}, application_state=#{applicationState}, reader_tel=#{readerTel}, reader_address=#{readerAddress}, remark=#{remark},
where purchase_no = #{purchaseNo}
