mapper.update:
update book
number=#{number}, type_id=#{typeId}, name=#{name}, author=#{author}, translator=#{translator}, press=#{press}, press_date=#{pressDate}, price=#{price}, borrow_time=#{borrowTime}, stock=#{stock},
where id =#{id}

mapper.select:
select * from book where 1=1
and type_id = #{typeId}
and name = #{name}

select * from book where 1=1
and type_id = #{typeId}
and name = #{name}
limit #{pager.startIndex},#{pager.pageSize}

select count(*) from book where 1=1
and type_id = #{typeId}
and name = #{name}
